
public class tiposNumericos {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		Byte numeroByte = 127; // -128 ATÉ 127
		
		Short numeroShort =  -32768; // -32768 até 32767
		
		Integer numeroInteger = 2147483647; // - 2147483648 até 2147483647 (2^31)
		
		Long numeroLong = 1L; // -9,223372037x10^18 até 9,223372037x10^18  (2^63)
	
		Float numeroFloat = 1.0f; // não usa muito pq precisa colococar f
		
		Double numeroDouble = 1.0; //mais usado pq não precisa colocar o f
	}

}
